<!-- Image Scroller JS --!> 


function div_scroll(){

	var $active = $("#scroller div.active");
	if ($active.length == 0) {
		$("#scroller div:last");
	}
	
	var $next = $active.next().length ? $active.next() : $("#scroller div:first");
	$active.addClass('last-active');
	$active.removeClass('active');
	
	$next.css({opacity: 0.0}).addClass('active').animate({opacity:1}, 1000, function(){
				$active.removeClass('active last-active');
		});
}

$(function(){

	setInterval("div_scroll()", 5000);
	
});


